display = "all";
let toggleBoxesDisplayed = () => {
    let e = document.forms.displayTabs.elements.toggleBoxes;
    for (let l = 0; l < e.length; l++)
      e[l].onclick = function () {
        (display = this.value),
          setDisplayState(),
          updateTabStyling(e[l].parentNode);
      };
  },
  buttons = document.querySelectorAll(".btn"),
  updateTabStyling = (e) => {
    for (let l = 0; l < buttons.length; l++)
      buttons[l].classList.remove("active"),
        buttons[l] === e && buttons[l].classList.add("active");
  };
function setDisplayState() {
  switch (display) {
    case "all":
      displayAllBoxes();
      break;
    case "even":
      displayAllBoxes(), removeOddBoxes();
      break;
    case "odd":
      displayAllBoxes(), removeEvenBoxes();
  }
}
toggleBoxesDisplayed();
let boxes = document.querySelectorAll(".box"),
  displayAllBoxes = () => {
    for (let e = 0; e < boxes.length; e++) boxes[e].style.display = "block";
  },
  removeEvenBoxes = () => {
    for (let e = 0; e < boxes.length; e++)
      e % 2 == 1 && (boxes[e].style.display = "none");
  },
  removeOddBoxes = () => {
    for (let e = 0; e < boxes.length; e++)
      e % 2 == 0 && (boxes[e].style.display = "none");
  };
